mapper.insert:
insert into Work(
  	work_seq
  	, work_name
  	, start_day
  	, period
  	, people
	, detail
	, progress
	, room_seq
) values
(
work_seq.nextval
, #{work_name}
, #{start_day}
, #{period}
, #{people}
, #{detail}
, #{progress}
, #{room_seq}
)

mapper.update:
update worker 
set room_seq = #{room_seq}
where id = #{id}
