mapper.insert:
insert into Worker
(
worker_seq
, name
, id
, password
, phone_number
, email
, room_seq
) values
(
worker_seq.nextval
, #{name}
, #{id}
, #{password}
, #{phone_number}
, #{email}
, #{room_seq}
)

mapper.update:
update worker 
set room_seq = #{room_seq}
where id = #{id}
